-- ----------------------------
-- Table structure for pw_property
-- ----------------------------
DROP TABLE IF EXISTS `pw_property`;
CREATE TABLE `pw_property` (
  `property_id` bigint NOT NULL AUTO_INCREMENT COMMENT '房产ID，主键，自增',
  `title` varchar(255) NOT NULL COMMENT '房产标题',
  `description` text COMMENT '房产描述',
  `price` decimal(15,2) NOT NULL COMMENT '房产价格',
  `address` varchar(255) DEFAULT '' COMMENT '房产地址',
  `city` varchar(100) DEFAULT '' COMMENT '城市',
  `postal_code` varchar(10) DEFAULT '' COMMENT '邮政编码',
  `country` varchar(50) DEFAULT 'UK' COMMENT '国家（默认UK）',
  `region` varchar(100) DEFAULT '' COMMENT '区域',
  `bedrooms` int DEFAULT '0' COMMENT '卧室数量',
  `bathrooms` int DEFAULT '0' COMMENT '卫生间数量',
  `garden_size` decimal(10,2) DEFAULT '0.00' COMMENT '花园面积（平方米）',
  `property_type` varchar(50) DEFAULT '' COMMENT '房产类型（独立屋、联排别墅、公寓等）',
  `build_year` year DEFAULT '2000' COMMENT '建造年份',
  `square_feet` decimal(10,2) DEFAULT '0.00' COMMENT '建筑面积（平方英尺）',
